    VirtualCan() {
        this->canbus = "vcan0";
        this->refreshTime = 100;

        checkStatus();
    }

    VirtualCan(char* canbus, int refresh_time){
        this->canbus = canbus;
        this->refreshTime = refresh_time;
        checkStatus();
    }

    void checkStatus(){
        if((s = socket(PF_CAN, SOCK_RAW, CAN_RAW)) < 0) {
            perror("Error while opening socket");
        }

        strcpy(ifr.ifr_name, canbus);
        ioctl(s, SIOCGIFINDEX, &ifr);

        addr.can_family  = AF_CAN;
        addr.can_ifindex = ifr.ifr_ifindex;

        if(bind(s, (struct sockaddr *)&addr, sizeof(addr)) < 0) {
            perror("Error in socket bind");
        }
    }

    void sendMessage(int id, int data){
        int data0 = data / 256;  // base 256
        int data1 = data % 256;
        frame.can_id  = id;
        frame.can_dlc = 2;
        frame.data[0] = data0;
        frame.data[1] = data1;

        nbytes = write(s, &frame, sizeof(struct can_frame));
    }

    void run() {
        int laps = 3;
        int lapTime = 168;  // lap 2'48"
        for (int lap = 0; lap < laps; lap++){
            int startTime = time(NULL);
            int dataToken = 0;
            int totToken = 1290;

            for (int i = 0; i < totToken; i++) {
                sendMessage(this->RPM, rpmData[i]);  // send bike data
                sendMessage(this->GEAR, gearData[i]);
                sendMessage(this->SPEED, speedData[i]);
                sendMessage(this->TEMPERATURE_BODY, 37);
                sendMessage(this->TEMPERATURE_OIL, tempOilData[i]);
                sendMessage(this->TEMPERATURE_EXT, 31);
                sendMessage(this->BPM, bpmData[i]);
                sendMessage(this->O2, oxygenData[i]);

                int currentTime = time(NULL) - startTime;  // send time
                if (currentTime < 45) {
                    sendMessage(this->TIME_S_1S, int(currentTime));
                } else if (currentTime < 45 + 41) {
                    sendMessage(this->TIME_S_2S, int(currentTime - 45));
                } else if (currentTime < 45 + 41 + 50) {
                    sendMessage(this->TIME_S_3S, int(currentTime - 45 - 41));
                } else {
                    sendMessage(this->TIME_S_4S, int(currentTime - 45 - 41 - 50));
                }

                sendMessage(this->GPS_X, gpsXData[i]);  // send gps data
                sendMessage(this->GPS_Y, gpsYData[i]);

                usleep(this->refreshTime * 1000);  // sleep
            }
        }
    }

